    <script>
        function close_modal() {
            $('#UserComments_continner').modal('hide');
        }

        function how_many_chars() {
            $InputText = $('#CommentText').val();
            $remain = 180 - $InputText.length;
            if ($remain <= 0) {
                $('#text_overload').removeClass('nested');
            } else {
                $('#text_overload').addClass('nested');
            }
            $('#remainchars').text($remain);

        }
    </script>
